import React, { useState } from 'react';

const Home = () => {
  const [teamA, setTeamA] = useState('');
  const [teamB, setTeamB] = useState('');
  const [kitAUrl, setKitAUrl] = useState('');
  const [kitAFile, setKitAFile] = useState(null);
  const [kitBUrl, setKitBUrl] = useState('');
  const [kitBFile, setKitBFile] = useState(null);
  const [referee1, setReferee1] = useState('');
  const [referee2, setReferee2] = useState('');
  const [youtube, setYoutube] = useState('');

  // For previewing uploaded images
  const kitAPreview = kitAFile ? URL.createObjectURL(kitAFile) : kitAUrl;
  const kitBPreview = kitBFile ? URL.createObjectURL(kitBFile) : kitBUrl;

  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center px-2 py-10 bg-gradient-to-br from-[#0f2027] via-[#2c5364] to-[#232526] relative overflow-hidden">
      {/* Techy background grid */}
      <div className="absolute inset-0 pointer-events-none z-0">
        <svg width="100%" height="100%" className="opacity-10">
          <defs>
            <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
              <path d="M 40 0 L 0 0 0 40" fill="none" stroke="#fff" strokeWidth="0.5" />
            </pattern>
          </defs>
          <rect width="100%" height="100%" fill="url(#grid)" />
        </svg>
      </div>

      {/* Logo ve Site İsmi */}
      <div className="flex flex-col items-center mb-14 select-none z-10">
        <img src="/logo192.png" alt="Logo" className="w-20 h-20 mb-4 drop-shadow-xl bg-white/10 rounded-full backdrop-blur-md border-2 border-blue-400/40" />
        <h1 className="text-5xl md:text-6xl font-extrabold text-white tracking-tight mb-2" style={{letterSpacing: '-0.03em', textShadow: '0 2px 24px #0ff, 0 1px 0 #222'}}>Futbol Analiz</h1>
        <span className="text-lg md:text-xl text-blue-200 font-light">Teknolojik Futbol Analiz u</span>
      </div>

      {/* Glassmorphism Card */}
      <div className="w-full max-w-4xl bg-white/10 backdrop-blur-2xl rounded-3xl shadow-2xl p-10 flex flex-col gap-10 border border-white/20 z-10">
        {/* Takımlar */}
        <div className="flex flex-col md:flex-row gap-8">
          {/* Takım A */}
          <div className="flex-1 p-0 md:p-1 rounded-2xl bg-gradient-to-br from-cyan-400/60 via-blue-600/30 to-blue-900/60 shadow-xl transition-transform hover:scale-[1.025] border-2 border-transparent hover:border-cyan-400/80">
            <div className="bg-white/10 rounded-2xl p-8 flex flex-col gap-4 h-full">
              <h2 className="text-xl font-bold text-cyan-300 mb-2 text-center tracking-tight uppercase drop-shadow-md border-b border-cyan-400/30 pb-2">Takım A</h2>
              <label className="block text-blue-100 font-medium">Takım İsmi</label>
              <input
                type="text"
                value={teamA}
                onChange={e => setTeamA(e.target.value)}
                className="w-full bg-white/20 border border-cyan-300/30 rounded-xl px-4 py-2 focus:outline-none focus:ring-2 focus:ring-cyan-400 transition-all text-lg shadow-sm text-white placeholder:text-cyan-200"
                placeholder="Takım A adı"
              />
              <label className="block text-blue-100 font-medium mt-2">Forma Görseli</label>
              <div className="flex flex-col gap-2">
                <input
                  type="file"
                  accept="image/*"
                  onChange={e => setKitAFile(e.target.files[0])}
                  className="file:mr-3 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-cyan-600 file:text-white hover:file:bg-cyan-700 bg-transparent text-white"
                />
                <input
                  type="text"
                  value={kitAUrl}
                  onChange={e => setKitAUrl(e.target.value)}
                  className="w-full bg-white/20 border border-cyan-300/30 rounded-xl px-4 py-2 focus:outline-none focus:ring-2 focus:ring-cyan-400 transition-all text-sm shadow-sm text-white placeholder:text-cyan-200"
                  placeholder="Google'dan forma görseli URL'si"
                />
                {(kitAPreview) && (
                  <img src={kitAPreview} alt="Forma A" className="w-24 h-24 object-contain rounded-xl border border-cyan-300/40 mx-auto mt-2 bg-white/20" />
                )}
              </div>
            </div>
          </div>
          {/* Takım B */}
          <div className="flex-1 p-0 md:p-1 rounded-2xl bg-gradient-to-br from-fuchsia-400/60 via-purple-600/30 to-indigo-900/60 shadow-xl transition-transform hover:scale-[1.025] border-2 border-transparent hover:border-fuchsia-400/80">
            <div className="bg-white/10 rounded-2xl p-8 flex flex-col gap-4 h-full">
              <h2 className="text-xl font-bold text-fuchsia-300 mb-2 text-center tracking-tight uppercase drop-shadow-md border-b border-fuchsia-400/30 pb-2">Takım B</h2>
              <label className="block text-purple-100 font-medium">Takım İsmi</label>
              <input
                type="text"
                value={teamB}
                onChange={e => setTeamB(e.target.value)}
                className="w-full bg-white/20 border border-fuchsia-300/30 rounded-xl px-4 py-2 focus:outline-none focus:ring-2 focus:ring-fuchsia-400 transition-all text-lg shadow-sm text-white placeholder:text-fuchsia-200"
                placeholder="Takım B adı"
              />
              <label className="block text-purple-100 font-medium mt-2">Forma Görseli</label>
              <div className="flex flex-col gap-2">
                <input
                  type="file"
                  accept="image/*"
                  onChange={e => setKitBFile(e.target.files[0])}
                  className="file:mr-3 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-fuchsia-600 file:text-white hover:file:bg-fuchsia-700 bg-transparent text-white"
                />
                <input
                  type="text"
                  value={kitBUrl}
                  onChange={e => setKitBUrl(e.target.value)}
                  className="w-full bg-white/20 border border-fuchsia-300/30 rounded-xl px-4 py-2 focus:outline-none focus:ring-2 focus:ring-fuchsia-400 transition-all text-sm shadow-sm text-white placeholder:text-fuchsia-200"
                  placeholder="Google'dan forma görseli URL'si"
                />
                {(kitBPreview) && (
                  <img src={kitBPreview} alt="Forma B" className="w-24 h-24 object-contain rounded-xl border border-fuchsia-300/40 mx-auto mt-2 bg-white/20" />
                )}
              </div>
            </div>
          </div>
        </div>
        {/* Hakemler */}
        <div className="flex flex-col md:flex-row gap-8">
          <div className="flex-1">
            <label className="block text-blue-100 font-semibold mb-1">Hakem 1</label>
            <input
              type="text"
              value={referee1}
              onChange={e => setReferee1(e.target.value)}
              className="w-full bg-white/20 border border-cyan-300/30 rounded-xl px-4 py-2 focus:outline-none focus:ring-2 focus:ring-cyan-400 transition-all text-lg shadow-sm text-white placeholder:text-cyan-200"
              placeholder="Hakem 1 adı"
            />
          </div>
          <div className="flex-1">
            <label className="block text-purple-100 font-semibold mb-1">Hakem 2</label>
            <input
              type="text"
              value={referee2}
              onChange={e => setReferee2(e.target.value)}
              className="w-full bg-white/20 border border-fuchsia-300/30 rounded-xl px-4 py-2 focus:outline-none focus:ring-2 focus:ring-fuchsia-400 transition-all text-lg shadow-sm text-white placeholder:text-fuchsia-200"
              placeholder="Hakem 2 adı"
            />
          </div>
        </div>
        {/* YouTube Linki */}
        <div>
          <label className="block text-blue-100 font-semibold mb-1">YouTube Linki</label>
          <input
            type="text"
            value={youtube}
            onChange={e => setYoutube(e.target.value)}
            className="w-full bg-white/20 border border-blue-300/30 rounded-xl px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all text-lg shadow-sm text-white placeholder:text-blue-200"
            placeholder="YouTube video bağlantısı"
          />
        </div>
        {/* Özet Butonu */}
        <div className="flex justify-center mt-2">
          <button
            className="bg-gradient-to-r from-cyan-400 via-blue-600 to-fuchsia-500 hover:from-cyan-500 hover:to-fuchsia-600 text-white font-bold py-3 px-16 rounded-full shadow-xl text-xl transition-all duration-200 transform hover:scale-105 focus:outline-none focus:ring-4 focus:ring-cyan-200/60 backdrop-blur-lg bg-opacity-80 border-2 border-white/20"
            style={{backdropFilter: 'blur(8px)', textShadow: '0 2px 16px #0ff, 0 1px 0 #222'}}
          >
            Özet
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
