import React, { useState, useEffect } from 'react';

const Home = () => {
  const [formData, setFormData] = useState({
    teamA: '',
    teamB: '',
    mainRef: '',
    sideRef: '',
    youtubeUrl: ''
  });
  
  const [teamAJersey, setTeamAJersey] = useState(null);
  const [teamBJersey, setTeamBJersey] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [loading, setLoading] = useState(false);
  const [analysisStatus, setAnalysisStatus] = useState('idle');
  const [analysisMessage, setAnalysisMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e, team) => {
    const file = e.target.files[0];
    if (file) {
      if (team === 'A') {
        setTeamAJersey(file);
      } else {
        setTeamBJersey(file);
      }
    }
  };

  const handleStartAnalysis = async (e) => {
    e.preventDefault();
    setLoading(true);
    setAnalysisStatus('starting');
    setAnalysisMessage('Analiz başlatılıyor...');
    
    try {
      const formDataToSend = new FormData();
      formDataToSend.append('team_a', formData.teamA);
      formDataToSend.append('team_b', formData.teamB);
      formDataToSend.append('main_ref', formData.mainRef || 'Main Referee');
      formDataToSend.append('side_ref', formData.sideRef || 'Side Referee');
      if (formData.youtubeUrl) {
        formDataToSend.append('youtube_url', formData.youtubeUrl);
      }
      
      if (teamAJersey) {
        formDataToSend.append('team_a_jersey', teamAJersey);
      }
      if (teamBJersey) {
        formDataToSend.append('team_b_jersey', teamBJersey);
      }

      const response = await fetch('http://localhost:8000/start-analysis', {
        method: 'POST',
        body: formDataToSend
      });

      const result = await response.json();
      
      if (result.status === 'success') {
        setAnalysisStatus('running');
        setAnalysisMessage('Analiz devam ediyor... OpenCV penceresi açılacak.');
        
        // Analiz durumunu periyodik olarak kontrol et
        const statusInterval = setInterval(async () => {
          try {
            const statusResponse = await fetch('http://localhost:8000/analysis-status');
            const statusResult = await statusResponse.json();
            
            if (statusResult.status === 'completed') {
              setAnalysisStatus('completed');
              setAnalysisMessage('✅ Analiz tamamlandı!');
              clearInterval(statusInterval);
              setLoading(false);
            } else if (statusResult.status === 'running') {
              setAnalysisMessage('🔄 Analiz devam ediyor...');
            }
          } catch (error) {
            console.error('Status check error:', error);
          }
        }, 2000);
        
        // 5 dakika sonra interval'i temizle
        setTimeout(() => {
          clearInterval(statusInterval);
          if (analysisStatus === 'running') {
            setAnalysisStatus('timeout');
            setAnalysisMessage('⏰ Analiz zaman aşımına uğradı');
            setLoading(false);
          }
        }, 300000);
        
      } else {
        setAnalysisStatus('error');
        setAnalysisMessage('❌ Analiz başlatılamadı');
        setLoading(false);
      }
      
    } catch (error) {
      console.error('Error:', error);
      setAnalysisStatus('error');
      setAnalysisMessage('❌ Bağlantı hatası');
      setLoading(false);
    }
  };

  const handlePrediction = async () => {
    if (!formData.teamA || !formData.teamB) {
      setPrediction('Lütfen iki takım adını da giriniz.');
      return;
    }

    setLoading(true);
    setPrediction('Tahmin yapılıyor, lütfen bekleyin...');

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('team_a', formData.teamA);
      formDataToSend.append('team_b', formData.teamB);

      const response = await fetch('http://localhost:8000/predict-match', {
        method: 'POST',
        body: formDataToSend
      });

      const result = await response.json();
      setPrediction(result.prediction);
    } catch (error) {
      setPrediction('Tahmin yapılırken hata oluştu.');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 relative overflow-hidden">
      {/* Tech Grid Background */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute inset-0" style={{
          backgroundImage: `
            linear-gradient(rgba(59, 130, 246, 0.1) 1px, transparent 1px),
            linear-gradient(90deg, rgba(59, 130, 246, 0.1) 1px, transparent 1px)
          `,
          backgroundSize: '50px 50px'
        }}></div>
      </div>

      <div className="relative z-10 container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          {/* Header */}
          <div className="text-center mb-12">
            <h1 className="text-6xl font-bold bg-gradient-to-r from-cyan-400 via-blue-500 to-purple-600 bg-clip-text text-transparent mb-4">
              Futbol Analiz AI
            </h1>
            <p className="text-xl text-gray-300">
              Yapay Zeka Destekli Maç Analizi ve Tahmin Sistemi
            </p>
          </div>

          {/* Main Form */}
          <div className="backdrop-blur-xl bg-white/5 rounded-3xl border border-white/20 shadow-2xl p-8 mb-8">
            <form onSubmit={handleStartAnalysis} className="space-y-6">
              {/* Team A Section */}
              <div className="grid md:grid-cols-2 gap-6">
                <div className="space-y-4">
                  <label className="block text-sm font-medium text-cyan-300">
                    Takım A
                  </label>
                  <input
                    type="text"
                    name="teamA"
                    value={formData.teamA}
                    onChange={handleInputChange}
                    placeholder="Takım A adını girin..."
                    className="w-full px-4 py-3 bg-black/30 border border-cyan-500/50 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:border-cyan-400 focus:ring-2 focus:ring-cyan-400/20 transition-all duration-300"
                  />
                  
                  <div>
                    <label className="block text-sm font-medium text-cyan-300 mb-2">
                      Takım A Forması
                    </label>
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handleFileChange(e, 'A')}
                      className="w-full px-4 py-3 bg-black/30 border border-cyan-500/50 rounded-xl text-white file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-medium file:bg-cyan-500/20 file:text-cyan-300 hover:file:bg-cyan-500/30 transition-all duration-300"
                    />
                  </div>
                </div>

                {/* Team B Section */}
                <div className="space-y-4">
                  <label className="block text-sm font-medium text-purple-300">
                    Takım B
                  </label>
                  <input
                    type="text"
                    name="teamB"
                    value={formData.teamB}
                    onChange={handleInputChange}
                    placeholder="Takım B adını girin..."
                    className="w-full px-4 py-3 bg-black/30 border border-purple-500/50 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:border-purple-400 focus:ring-2 focus:ring-purple-400/20 transition-all duration-300"
                  />
                  
                  <div>
                    <label className="block text-sm font-medium text-purple-300 mb-2">
                      Takım B Forması
                    </label>
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handleFileChange(e, 'B')}
                      className="w-full px-4 py-3 bg-black/30 border border-purple-500/50 rounded-xl text-white file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-medium file:bg-purple-500/20 file:text-purple-300 hover:file:bg-purple-500/30 transition-all duration-300"
                    />
                  </div>
                </div>
              </div>

              {/* Referees Section */}
              <div className="grid md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-yellow-300 mb-2">
                    Ana Hakem
                  </label>
                  <input
                    type="text"
                    name="mainRef"
                    value={formData.mainRef}
                    onChange={handleInputChange}
                    placeholder="Ana hakem adını girin..."
                    className="w-full px-4 py-3 bg-black/30 border border-yellow-500/50 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:border-yellow-400 focus:ring-2 focus:ring-yellow-400/20 transition-all duration-300"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-orange-300 mb-2">
                    Yan Hakem
                  </label>
                  <input
                    type="text"
                    name="sideRef"
                    value={formData.sideRef}
                    onChange={handleInputChange}
                    placeholder="Yan hakem adını girin..."
                    className="w-full px-4 py-3 bg-black/30 border border-orange-500/50 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:border-orange-400 focus:ring-2 focus:ring-orange-400/20 transition-all duration-300"
                  />
                </div>
              </div>

              {/* YouTube URL */}
              <div>
                <label className="block text-sm font-medium text-red-300 mb-2">
                  YouTube Maç Linki (Opsiyonel)
                </label>
                <input
                  type="url"
                  name="youtubeUrl"
                  value={formData.youtubeUrl}
                  onChange={handleInputChange}
                  placeholder="https://www.youtube.com/watch?v=..."
                  className="w-full px-4 py-3 bg-black/30 border border-red-500/50 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:border-red-400 focus:ring-2 focus:ring-red-400/20 transition-all duration-300"
                />
              </div>

              {/* Action Buttons */}
              <div className="flex flex-col sm:flex-row gap-4 pt-6">
                <button
                  type="submit"
                  disabled={loading || analysisStatus === 'running'}
                  className="flex-1 px-8 py-4 bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white font-bold rounded-xl shadow-lg hover:shadow-green-500/25 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? 'Başlatılıyor...' : '🎬 Özeti Başlat'}
                </button>
                
                <button
                  type="button"
                  onClick={handlePrediction}
                  disabled={loading || !formData.teamA || !formData.teamB}
                  className="flex-1 px-8 py-4 bg-gradient-to-r from-purple-500 to-pink-600 hover:from-purple-600 hover:to-pink-700 text-white font-bold rounded-xl shadow-lg hover:shadow-purple-500/25 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? 'Tahmin Yapılıyor...' : '🤖 Maç Tahmini Yap'}
                </button>
              </div>
            </form>
          </div>

          {/* Analysis Status */}
          {analysisStatus !== 'idle' && (
            <div className="backdrop-blur-xl bg-white/5 rounded-3xl border border-white/20 shadow-2xl p-6 mb-8">
              <h3 className="text-xl font-bold text-blue-400 mb-4">📊 Analiz Durumu</h3>
              <div className="bg-black/30 rounded-xl p-4 border border-blue-500/30">
                <p className="text-white text-lg">{analysisMessage}</p>
                {analysisStatus === 'running' && (
                  <div className="mt-4">
                    <div className="animate-pulse bg-blue-500 h-2 rounded-full"></div>
                    <p className="text-gray-300 text-sm mt-2">OpenCV penceresi açılacak, analiz devam ediyor...</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Prediction Result */}
          {prediction && (
            <div className="backdrop-blur-xl bg-white/5 rounded-3xl border border-white/20 shadow-2xl p-6 mb-8">
              <h3 className="text-xl font-bold text-green-400 mb-4">🤖 AI Tahmin Sonucu</h3>
              <div className="bg-black/30 rounded-xl p-4 border border-green-500/30">
                <p className="text-white text-lg">{prediction}</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
