import React, { useState } from 'react';

const Home = () => {
  const [teamA, setTeamA] = useState('');
  const [kit, setKit] = useState('');
  const [referee, setReferee] = useState('');
  const [youtube, setYoutube] = useState('');

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center pt-16">
      {/* Logo ve Site İsmi */}
      <div className="flex items-center mb-10">
        <img src="/logo192.png" alt="Logo" className="w-14 h-14 mr-4" />
        <h1 className="text-3xl md:text-4xl font-bold text-blue-700 tracking-wide">Futbol Analiz</h1>
      </div>

      {/* Inputlar */}
      <div className="flex flex-col gap-6 w-full max-w-2xl bg-white p-8 rounded-xl shadow-lg">
        <div className="flex flex-col md:flex-row gap-4 w-full">
          <div className="flex-1">
            <label className="block text-gray-700 font-semibold mb-1">Takım İsmi A</label>
            <input
              type="text"
              value={teamA}
              onChange={e => setTeamA(e.target.value)}
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="Takım adı girin"
            />
          </div>
          <div className="flex-1">
            <label className="block text-gray-700 font-semibold mb-1">Forma</label>
            <input
              type="text"
              value={kit}
              onChange={e => setKit(e.target.value)}
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="Forma rengi/numarası"
            />
          </div>
          <div className="flex-1">
            <label className="block text-gray-700 font-semibold mb-1">Hakem 1</label>
            <input
              type="text"
              value={referee}
              onChange={e => setReferee(e.target.value)}
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="Hakem adı"
            />
          </div>
        </div>
        <div>
          <label className="block text-gray-700 font-semibold mb-1">YouTube Linki</label>
          <input
            type="text"
            value={youtube}
            onChange={e => setYoutube(e.target.value)}
            className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-red-400"
            placeholder="YouTube video bağlantısı"
          />
        </div>
      </div>
    </div>
  );
};

export default Home;
